# Insert data into the MySQL table
def insert_into_sales_table(connection, date, bill_no,tablet):
    cursor = connection.cursor()
    sql_query = "INSERT INTO sales (Date, Bill_No, TabletName) VALUES (%s, %s,%s)"
    cursor.execute(sql_query, (date, bill_no,tablet))
    connection.commit()
    cursor.close()

# Get the current date and time

def generate_bill_no(date):

   
    if random.randint(0, 1) == 0:
        # Use the same time
        
        bill_time = current_time
    else:
        # Use a different time between 9am to 9pm
        bill_time = current_time.replace(hour=random.randint(9, 20), minute=random.randint(0, 59), second=random.randint(0, 59))

    # Format the bill number
    bill_no = "BILL-" + date.replace("-","") + bill_time.strftime("%H%M%S")
    return bill_no

